import React, {useState, useEffect} from 'react'
import {useParams, useNavigate} from 'react-router-dom'
import {useSelector} from 'react-redux'
import axios from 'axios'
import {showSuccessMsg, showErrMsg} from '../../utils/notification/Notification'

const initialState = {
    title:'',
    price: 0,
    description: '',
    location:'',
    category:'',
    phone:'',
    isArchived: 0,
    err: '',
    success:''
  }

function EditProduct() {
    const {id} = useParams()
    const products = useSelector(state => state.products)
    const token = useSelector(state => state.token)
    const [editProduct, setProduct] = useState(initialState)
    const { title, price, description, location, category, phone, isArchived, err, success } = editProduct

    if(products.length !== 0){
        products.forEach(product => {
            if(product._id === id){
                setProduct(editProduct)
            }
        })
    }

    const navigate = useNavigate()
    // const [editProduct, setEditProduct] = useState([])

    const [checkArchive, setCheckArchive] = useState(false)
    // const [err, setErr] = useState(false)
    // const [success, setSuccess] = useState(false)
    const [num, setNum] = useState(0)

    // useEffect(() => {
    //     if(products.length !== 0){
    //         products.forEach(product => {
    //             if(product._id === id){
    //                 setEditProduct(product)
    //                 setCheckProduct(product.role === 1 ? true : false)
    //             }
    //         })
    //     }else{
    //         navigate('/profile')
    //     }
    // },[product, id, navigate])

    const handleChangeInput = e => {
        const { name, value } = e.target
        setProduct({...product,[name]:value, err:'', success:''})
  }

    const handleUpdate = async () => {
        try {
            if(num % 2 !== 0){
                const res = await axios.patch(`/api/product/${editProduct._id}`, {
                    isArchived: checkArchive ? 1 : 0
                }, {
                    headers: {Authorization: token}
                })

                setSuccess(res.data.msg)
                setNum(0)
            }
        } catch (err) {
            err.response.data.msg && setErr(err.response.data.msg)
        }
    }

    const changeAvatar = async(e) => {
        e.preventDefault()
        try {
            const file = e.target.files[0]
  
            if(!file) return setProduct({...product, err: "No files were uploaded." , success: ''})
  
            if(file.size > 1024 * 1024)
                return setProduct({...product, err: "Size too large." , success: ''})
  
            if(file.type !== 'image/jpeg' && file.type !== 'image/png')
                return setProduct({...product, err: "File format is incorrect." , success: ''})
  
            let formData =  new FormData()
            formData.append('file', file)
  
            setLoading(true)
            const res = await axios.post('/api/upload_avatar', formData, {
                headers: {'content-type': 'multipart/form-data', Authorization: token}
            })
  
            setLoading(false)
            setAvatar(res.data.url)
            
        } catch (err) {
            setProduct({...product, err: err.response.data.msg , success: ''})
        }
    }

    const handleCheck = () => {
        setSuccess('')
        setErr('')
        setCheckArchive(!checkArchive)
        setNum(num + 1)
    }

    return (
        <div className="profile_page edit_user">
            <div className="row">
                <button onClick={() => navigate(-1)} className="go_back">
                    <i className="fas fa-long-arrow-alt-left"></i> Go Back
                </button>
            </div>

            <div className="col-left">
                <h2>Edit User</h2>

                <div className="avatar">
                    <img src={avatar ? avatar : user.avatar} alt=""/>
                    <span>
                        <i className="fas fa-camera"></i>
                        <p>Change</p>
                        <input type="file" name="file" id="file_up" onChange={changeAvatar} />
                    </span>
                </div>

                <div>
                    <label htmlFor="title">Title</label>
                    <input type="text" name="title" id="title" placeholder="Enter Title" value={editProduct.title} onChange={handleChangeInput}/>
                </div>

                <div>
                    <label htmlFor="price">Price</label>
                    <input type="number" name="price" id="price" placeholder="Enter Price" value={editProduct.price} onChange={handleChangeInput}/>
                </div>

                <div>
                    <label htmlFor="description">Description</label>
                    <textarea type="text" name="description" id="description" placeholder="Enter Description" value={editProduct.description} onChange={handleChangeInput}/>
                </div>

                <div>
                    <label htmlFor="location">Location</label>
                    <input type="text" name="location" id="location" placeholder="Enter Location" value={editProduct.location} onChange={handleChangeInput}/>
                </div>

                <div>
                    <label htmlFor="category">Category</label>
                    <input type="text" name="category" id="category" placeholder="Enter Category" value={editProduct.category} onChange={handleChangeInput}/>
                </div>

                <div>
                    <label htmlFor="phone">Phone no.</label>
                    <input type="text" name="phone" id="phone"placeholder="Enter Phone Number" value={editProduct.phone} onChange={handleChangeInput}/>
                </div>

                <div className="form-group">
                    <input type="checkbox" id="isArchive" checked={checkArchive}
                    onChange={handleCheck} />
                    <label htmlFor="isArchive">isArchived</label>
                </div>

                <button onClick={handleUpdate}>Update</button>

                {err && showErrMsg(err)}
                {success && showSuccessMsg(success)}
            </div>
        </div>
    )
}

export default EditProduct
